//import x from "../cardView"
import axios from "axios";
import { useEffect, useState  } from "react";

export default function Reducer() {

    const [data, setData] = useState([]);

    useEffect(() => {
      axios.get(
          "https://api.themoviedb.org/3/movie/popular?api_key=2e5d307a9142003b321ca6029b44fe63"
        )
        .then((res) => {
          setData(res.data.results);
        })
        .catch((err) => {
          console.log(err);
        });
    }, []);


    const INITIAL_STATE = {

        dataD: data,
        cart: [],
        currentItem: null,

    }


    function movieReducer(state= INITIALL_STATE, action)
    {
        switch(action.type){
            case "SET_LANG":
                return {
                    ...state,
                    lang: action.payload
                }
            default: 
                return state
        }





  }




}
