const INITIAL_STATE = {
  cart : []
}


export default function MovieReducer(state = INITIAL_STATE , action){


  switch(action.type){
    case "ADD_TO_CART":
      return {
        ...state,
        cart: [].push(action.payload.id)
      }
    case "DEL_FROM_CART":
      return {
        ...state,
        cart: [action.payload.id]
      }  
    default: 
      return state
  }




}

