import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState } from "react";
// import React, { useState } from "react";
// import './css/style.css';
 //import {useParams} from "react-router-dom";
import { Row, Col, Card, Container  } from "react-bootstrap";
import {Link} from "react-router-dom";

import {connect} from 'react-redux'
const FavMovie = (props) => {


    const [data, setData] = useState([]);

    useEffect(() => {
      axios.get("https://api.themoviedb.org/3/movie/popular?api_key=2e5d307a9142003b321ca6029b44fe63")
        .then((res) => {
          setData(res.data.results);
        })
        .catch((err) => {
          console.log(err);
        });
    }, []);



    const cartt = props.cart
  return (
      <>

      <h1>FAV MOVIE</h1>
      <Container>
      <Row xs={1} md={3} className="g-4">
          {cartt.map((cartId , index) => {
              return (


              <Col key={index}> 
              <Card>
                <Card.Img variant="top" src={`https://image.tmdb.org/t/p/w500/${cartId.poster_path}`} />
                <Card.Body>
                  <Card.Title>{cartId.title}</Card.Title>
                  <Card.Text>
                    <Link  to={`/film/${cartId.id}`}>Show details</Link> 
                    {/* <button type="button" className="btn btn-primary"  onClick={()=> addFav(resdata.id)} >delete</button> */}

                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>

              );
          })}
      </Row>
      </Container>
      </>
  );





};

function mapStateToProps(state){
    return {
        cart: state.cart
    }
 }

export default connect(mapStateToProps)(FavMovie);
