import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState  } from "react";
import {Link} from "react-router-dom";

// import React, { useState } from "react";
// import './css/style.css';
import { Row, Col, Card, Container, Button } from "react-bootstrap";

const CardView = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/movie/popular?api_key=2e5d307a9142003b321ca6029b44fe63"
      )
      .then((res) => {
        setData(res.data.results);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  console.log(data);




  function show(){

    return <><Link  key={data.id} to={`/film/${data.id}`} /></>
  }


  return (
    <>



       <Container>
        <Row xs={1} md={3} className="g-4">
      {data.map((resdata, index) => {
        console.log(resdata.title);
        return (
              <Col key={index}> 
                <Card>
                  <Card.Img variant="top" />
                  <Card.Body>
                    <Card.Title>{resdata.title}</Card.Title>
                    <Card.Text>
                     {/* <Button className="btn-danger" onClick={() => show()}>Show details</Button> */}
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>

        );
      })}
          </Row>
          </Container>
    </>
  );
};

export default CardView;
