const INITIAL_STATE = {
  cart : []
}


export default function Reducer(state = INITIAL_STATE , action){


  switch(action.type){
    case "ADD_TO_CART":
      return {
        ...state,
        lang: action.payload
      }
    case "DEL_FROM_CART":
      return {
        ...state,
        id: action.payload
      }  
    default: 
      return state
  }


}
