import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import './css/style.css';
import {Container} from 'react-bootstrap';



const SignUp = () => {

    const [info , setInfo] = useState({
        name: "",
        email: "",
        username: "",
        password: "",
        confirmPass: ""
    })


    const [err , setErr] = useState({
        nameV: null,
        emailV: null,
        usernameV: null,
        passwordV: null,
        confirmPassV: null
    })


    const handelSubmet = (e) =>{
        e.preventDefault();
    }



    const isValidEmail = email => {
        const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(String(email).toLowerCase());
    }


    const changeValue = (e) => {


      if(e.target.name === "name"){
        setInfo({
          ...info,
          name: e.target.value
        })

        setErr({
          ...err,
          emailV: 
            e.target.value.length === 0 ? 
            "please email is requared"
                      : !isValidEmail(e.target.value) ?
                       "Provide a valid email address"
            : null
        })


      }
      else if(e.target.name === "email") {
			setInfo({
				...info,
				email: e.target.value
			})

            setErr({
				...err,
				emailV: 
					e.target.value.length === 0 ? 
					"please email is requared"
                    : !isValidEmail(e.target.value) ?
                     "Provide a valid email address"
					: null
			})


    } 

    else if (e.target.name === "psw"){
        setInfo({
            ...info,
            password: e.target.value
        })
        setErr({
            ...err,
            passwordV: 
                e.target.value.length === 0 ? 
                "please password is required"
                : e.target.value.length < 8 ? 
                "passwoed should be equal or more than 8 character"
                : null
        })
    }



}

const [passwordShown, setPasswordShown] = useState(false);

const togglePassword = (e , ch) => {

     if(e.target.name === "ch"){
        setPasswordShown(!passwordShown);

    }
  };









  return (
    <>


   <Container>

   <h2>SignUp Form</h2>

<form onSubmit={(e)=> handelSubmet(e)}>

  <div className="container">

  <label htmlFor="name"><b>Name</b></label>
    <input type="text" 
    placeholder="your name"
    name="name" required
    value={info.name}
    onChange={(e) => changeValue(e)}

    />
    <small className="text-danger">{err.nameV}</small>
    <br />

    <label htmlFor="email"><b>ŸèEmail</b></label>
    <input type="text" 
    placeholder="your email"
    name="email" required
    value={info.email}
    onChange={(e) => changeValue(e)}


    />
    <small className="text-danger">{err.emailV}</small>
    <br />

  <label htmlFor="uname"><b>UserName</b></label>
    <input type="text" 
    placeholder="your username"
    name="uname" required
    value={info.username}
    onChange={(e) => changeValue(e)}


    />
   <small className="text-danger">{err.usernameV}</small>

    <br />
    <label htmlFor="psw"><b>Password</b></label>
    <input type={passwordShown ? "text" : "password"}
     placeholder="Enter Password" 
     name="psw" required 
     value={info.password}
      onChange={(e) => changeValue(e) } 
     /> 
    <small className="text-danger">{err.passwordV}</small>
    <br />
    <label htmlFor="Cpsw"><b>Confirm Password</b></label>
    <input type={passwordShown ? "text" : "password"}
     placeholder="Enter Password" 
     name="Cpsw" required 
     value={info.confirmPass}
      onChange={(e) => changeValue(e) } 
     /> 
    <small className="text-danger">{err.confirmPassV}</small>
    <br />   
    <input type="checkbox" name="ch" onChange={(e) => togglePassword(e)} /> Show Password

    <button type="submit">SignUp</button>

  </div>


</form>

   </Container>





    </>
  );
}

export default SignUp;
