import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import './css/style.css';
import {Container} from 'react-bootstrap';




const Login = () => {

    const [info , setInfo] = useState({
        email: "ahmed@yahoo.com",
        password: ""
    })


    const [err , setErr] = useState({
        emailV: null,
        passwordV: null
    })


    const handelSubmet = (e) =>{
        e.preventDefault();
    }



    const isValidEmail = email => {
        const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return re.test(String(email).toLowerCase());
    }


    const changeValue = (e) => {

           if(e.target.name === "email") {
			setInfo({
				...info,
				email: e.target.value
			})

            setErr({
				...err,
				emailV: 
					e.target.value.length === 0 ? 
					"please email is requared"
                    : !isValidEmail(e.target.value) ?
                     "Provide a valid email address"
					: "Success"
			})


    } 

    else if (e.target.name === "password"){
        setInfo({
            ...info,
            password: e.target.value
        })
        setError({
            ...error,
            titleValidate: 
                e.target.value.length === 0 ? 
                "please insert your title"
                : e.target.value.length < 3 ? 
                "please enter a valid title"
                : null
        })
    }

}


  return (
    <>


   <Container>

   <h2>Login Form</h2>

<form onSubmit={(e)=> handelSubmet(e)}>
  <div className="imgcontainer">
    <img src={require('./img/meC.png')} alt='Avatar' className='avatar' />
  </div>

  <div className="container">
    <label for="email"><b>ُEmail</b></label>
    <input type="text" 
    placeholder="your email"
    name="email" required
    value={info.email}
    onChange={(e) => changeValue(e)}


    />
    <small className="text-danger">{err.emailV}</small>

    <br />
    <label for="psw"><b>Password</b></label>
    <input type="password" placeholder="Enter Password" name="psw" required /> 
    <span>error</span>
    <br />   
    <button type="submit">Login</button>
    <label>
      <input type="checkbox" checked="checked" name="remember" /> Remember me
    </label>
  </div>


</form>

   </Container>





    </>
  );
}

export default Login;
